mapper.select:
SELECT #{description} AS description, COUNT(1) AS cnt, NVL(SUM(TRADE_MONY),0) AS money FROM ( SELECT C_TRADE_NO, A.SITE_CD, ORDER_NO, CARD_CD, CARD_NO, APP_NO, QUOTA_MONTH, TRADE_MONY, PG_FEE, PG_FEE_TAX, CARD_FEE, ORDER_TELNO, RCV_TELNO, PG_VAN_STATUS, AUTH_YM, AUTH_DAY, AUTH_HM, ORDER_NM, B.SITE_NAME FROM ( SELECT C_TRADE_NO, SITE_CD, ORDER_NO, CARD_CD, CARD_NO, APP_NO, QUOTA_MONTH, TRADE_MONY, PG_FEE, PG_FEE_TAX, CARD_FEE, ORDER_TELNO, RCV_TELNO, PG_VAN_STATUS, AUTH_YM, AUTH_DAY, AUTH_HM, ORDER_NM FROM TBLCARD_TRADE WHERE AUTH_YM||AUTH_DAY BETWEEN (#{fr_ymd}) AND (#{to_ymd})
AND site_cd IN (#{site_cd, jdbcType=VARCHAR})
AND corp_id IN (#{corp_id, jdbcType=VARCHAR})
AND pg_van_status IN (${trade_status})
AND order_no = #{search_word, jdbcType = VARCHAR}
AND goods_cd like #{search_word, jdbcType = VARCHAR} %
AND order_nm like #{search_word, jdbcType = VARCHAR} %
AND c_trade_no = #{search_word, jdbcType = VARCHAR}
AND card_no like #{search_word, jdbcType = VARCHAR} %
AND auth_no like #{search_word, jdbcType = VARCHAR} %
AND mob_no_chg like #{search_word, jdbcType = VARCHAR} %
AND trade_mony = #{search_keyword, jdbcType = VARCHAR}
) A, TBLSITE_INFO B, TBLCARD_MASTER C WHERE A.SITE_CD = B.SITE_CD AND B.SITE_CD = C.SITE_CD AND CARD_AUTH_TYPE = 'AT01' )

SELECT #{description} AS description, COUNT(1) AS cnt , NVL(SUM(TRADE_MONY),0) AS money FROM ( SELECT C_TRADE_NO, A.SITE_CD, ORDER_NO, CARD_CD, CARD_NO, APP_NO, QUOTA_MONTH, TRADE_MONY, PG_FEE, PG_FEE_TAX, PG_NO_FEE, ORDER_TELNO, MEIP_STATUS, MEIP_YM, MEIP_DAY, ORDER_NM , JUNG_DATE, BORYU_CODE, BORYU_DESC , CORP_TAX_NO, B.SITE_NAME FROM ( SELECT C_TRADE_NO, SITE_CD, ORDER_NO, CARD_CD, CARD_NO, APP_NO, QUOTA_MONTH, TRADE_MONY, PG_FEE, PG_FEE_TAX, PG_NO_FEE, ORDER_TELNO, MEIP_STATUS, MEIP_YM, MEIP_DAY, ORDER_NM , JUNG_DATE, BORYU_CODE, BORYU_DESC , CORP_TAX_NO FROM TBLCARD_MEIP WHERE MEIP_YM||MEIP_DAY BETWEEN #{fr_ymd} AND #{to_ymd}
AND site_cd IN (#{site_cd, jdbcType=VARCHAR})
AND corp_id IN (#{corp_id, jdbcType=VARCHAR})
AND meip_status in (${trade_status})
AND order_no = #{search_word, jdbcType = VARCHAR}
AND goods_cd like #{search_word, jdbcType = VARCHAR} %
AND order_nm like #{search_word, jdbcType = VARCHAR} %
AND c_trade_no = #{search_word, jdbcType = VARCHAR}
AND card_no like #{search_word, jdbcType = VARCHAR} %
AND app_no like #{search_word, jdbcType = VARCHAR} %
AND van_tx_id like #{search_word, jdbcType = VARCHAR} %
) A, TBLSITE_INFO B WHERE A.SITE_CD = B.SITE_CD )
